-- ----------------------------
-- Procedure structure for proc_batch_deduct_sku_stock
-- ----------------------------
DROP PROCEDURE IF EXISTS `proc_batch_deduct_sku_stock`;
delimiter ;;
CREATE PROCEDURE `proc_batch_deduct_sku_stock`(IN p_order_id INT,
    IN p_sku_list JSON)
BEGIN
    DECLARE v_index INT DEFAULT 0;
    DECLARE v_count INT;
    DECLARE v_sku_id BIGINT;
    DECLARE v_quantity INT;
    DECLARE v_current_stock INT;
    DECLARE v_error_msg VARCHAR(255);
    
    -- 声明异常处理
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
        RESIGNAL;
    END;
    
    -- 开启事务
    START TRANSACTION;
    
    -- 获取SKU列表长度
    SET v_count = JSON_LENGTH(p_sku_list);
    
    -- 循环处理每个SKU
    WHILE v_index < v_count DO
        -- 获取SKU ID和数量
        SET v_sku_id = JSON_EXTRACT(p_sku_list, CONCAT('$[', v_index, '].skuId'));
        SET v_quantity = JSON_EXTRACT(p_sku_list, CONCAT('$[', v_index, '].quantity'));
        
        -- 检查库存
        SELECT stock INTO v_current_stock 
        FROM product_sku 
        WHERE sku_id = v_sku_id 
        FOR UPDATE;
        
        IF v_current_stock IS NULL THEN
            SET v_error_msg = CONCAT('SKU不存在: ', v_sku_id);
            SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = v_error_msg;
        END IF;
        
        IF v_current_stock < v_quantity THEN
            SET v_error_msg = CONCAT('SKU库存不足: ', v_sku_id, ', 当前库存: ', v_current_stock, ', 需要: ', v_quantity);
            SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = v_error_msg;
        END IF;
        
        -- 扣减库存
        UPDATE product_sku 
        SET stock = stock - v_quantity,
            version = version + 1
        WHERE sku_id = v_sku_id;
        
        -- 记录日志
            sku_id, 
            order_id, 
            change_type, 
            change_quantity, 
            before_stock, 
            after_stock,
            remark
        ) VALUES (
            v_sku_id,
            p_order_id,
            'DEDUCT',
            -v_quantity,
            v_current_stock,
            v_current_stock - v_quantity,
            CONCAT('订单扣减库存: ', p_order_id)
        );
        
        SET v_index = v_index + 1;
    END WHILE;
    
    -- 提交事务
    COMMIT;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for proc_clean_expired_cart_items
-- ----------------------------
DROP PROCEDURE IF EXISTS `proc_clean_expired_cart_items`;
delimiter ;;
CREATE PROCEDURE `proc_clean_expired_cart_items`()
BEGIN
  -- 删除超过30天未更新的购物车项
  DELETE FROM `cart` WHERE `update_time` < DATE_SUB(NOW(), INTERVAL 30 DAY);
  
  -- 删除已经标记为过期的购物车项
  DELETE FROM `cart` WHERE `expire_time` IS NOT NULL AND `expire_time` < NOW();
  
  -- 将已下单的购物车项标记为已下单状态
  UPDATE `cart` SET `status` = 2 
  WHERE `cart_id` IN (
    SELECT c.cart_id FROM `cart` c
    JOIN `order_goods` og ON c.goods_id = og.goods_id 
    JOIN `order` o ON og.order_id = o.order_id
    WHERE c.user_id = o.user_id AND o.create_time > c.create_time
  );
END
;;
delimiter ;

