  -- 生成规格哈希值
  IF NEW.specs IS NOT NULL THEN
    SET NEW.specs_hash = MD5(JSON_UNQUOTE(JSON_EXTRACT(NEW.specs, '$')));
  ELSE
    SET NEW.specs_hash = MD5('');
  END IF;
END
