delimiter ;;
CREATE TRIGGER `trg_user_coupon_expire_check` BEFORE UPDATE ON `user_coupon` FOR EACH ROW BEGIN
  -- 如果过期时间已到且状态为未使用，则修改状态为已过期
  IF NEW.expire_time < NOW() AND NEW.status = 'UNUSED' THEN
    SET NEW.status = 'EXPIRED';
  END IF;
END
